     $.ajax({        url: BASE_URL+'Admin/Clients/remove_building/'+bid,        dataType: 'text',        success: function(data){          if(data == 1){            location.reload();          }else{            alert("Unable to delete this. Please try again later");          }        }      });    }  }</script></body></html>