disel').hide();		$('.fire-water').hide();		$('.fire-pressure').show();	}}function addmorepumps(){   var count = parseInt($("#pumpCount").val())+1;   if(count<6){		   $("#pumpdetails"+count).show();	   $("#pumpCount").val(count);	   		   }else{	   alert('You reached max limit');   }               }function addmoredisels(){   var count = parseInt($("#diselCount").val())+1;   if(count<6){		   $("#diseldetails"+count).show();	   $("#diselCount").val(count);	   		   }else{	   alert('You reached max limit');   }               }function addmorewaters(){   var count = parseInt($("#waterCount").val())+1;   if(count<6){		   $("#waterdetails"+count).show();	   $("#waterCount").val(count);	   		   }else{	   alert('You reached max limit');   }               }function addmorepressures(){   var count = parseInt($("#pressureCount").val())+1;   if(count<6){		   $("#pressuredetails"+count).show();	   $("#pressureCount").val(count);	   		   }else{	   alert('You reached max limit');   }               }function removepump(row){     $("#pumpdetails"+row).hide();   	   $("#pumpdetails"+row).remove();	             }function removedisel(row){     $("#diseldetails"+row).hide();   	   $("#diseldetails"+row).remove();	             }function removewater(row){     $("#waterdetails"+row).hide();   	   $("#waterdetails"+row).remove();	             }function removepressure(row){     $("#pressuredetails"+row).hide();   	   $("#pressuredetails"+row).remove();	             }</script></body></html>